    // ===============================
    // category
    // ===============================
    function addCategory() {
        var name = '';
        Swal.fire({
            title: 'Kategori adını giriniz!',
            input: 'text',
            inputAttributes: {
                autocapitalize: 'off'
            },
            showCancelButton: true,
            cancelButtonText: 'Vazgeç',
            confirmButtonText: 'Ekle',
            showLoaderOnConfirm: true,
            preConfirm: (login) => {
                if (login != '') {
   ");
            WriteLiteral(@"                 name = login;
                }
                else {
                    Swal.showValidationMessage(
                        `İsim alanı boş bırakılamaz!`
                    )
                }
            },
            allowOutsideClick: () => !Swal.isLoading()
        }).then((result) => {
            if (result.isConfirmed) {
                $.ajax({
                    url: '/Category/Add/',
                    type: 'POST',
                    dataType: 'json',
                    data: {
                        'Name': name,
                    },
                    success: function (data) {
                        Swal.fire({
                            position: 'center',
                            icon: 'success',
                            title: 'Başarıyla eklenmiştir!',
                            showConfirmButton: false,
                            timer: 800
                        }).then(() => {
                            location.reload();");
            WriteLiteral(@"
                        })
                    },
                    error: function (data) {
                    }
                });
            }
        })
    }
    // ===============================
    function getCategories() {
        $.ajax({
            type: ""GET"",
            url: ""/Category/GetAll"",
            data: ""{}"",
            success: function (data) {
                var s = '';
                for (var i = 0; i < data.categories.$values.length; i++) {
                    s += '<option value=""' + data.categories.$values[i].id + '"">' + data.categories.$values[i].name + '</option>';
                }
                $(""#category"").html(s);
                getSubCategories();
            }
        });
    }
    // ===============================
    function deleteCategory() {
        var id = $(""#category"").val();
        console.log(id);
        Swal.fire({
            title: 'Silmek istediğinize emin misiniz?',
            text: ""Silme işlemi geri alına");
            WriteLiteral(@"maz! Bu kategoriye ait tüm ürünler silinecektir!"",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#d33',
            cancelButtonColor: '#3085d6',
            cancelButtonText: 'Vazgeç',
            confirmButtonText: 'Evet, sil!'
        }).then((result) => {
            if (result.isConfirmed) {
                $.ajax({
                    type: ""POST"",
                    url: ""/Category/Delete/"" + id,
                    success: function (result) {
                        Swal.fire({
                            position: 'center',
                            icon: 'success',
                            title: 'Başarıyla silinmiştir!',
                            showConfirmButton: false,
                            timer: 800
                        }).then(() => {
                            location.reload();
                        })

                    },
                    error: function (data) {
                    }
 ");
            WriteLiteral(@"               });
            }
        })
    }
